"use client";
import React, { useState, useEffect } from "react";
import Sidebar from "@/components/AdminComponents/Sidebar";
import DashboardHome from "@/components/AdminComponents/DashboardHome";
import HeroManager from "@/components/AdminComponents/Sections/HeroManager";
import ServicesManager from "@/components/AdminComponents/Sections/ServicesManager";
import ContactManager from "@/components/AdminComponents/Sections/ContactManager";
import AboutManager from "@/components/AdminComponents/Sections/AboutManager";
import PortfolioManager from "@/components/AdminComponents/Sections/PortfolioManager";
import BlogManager from "@/components/AdminComponents/Sections/BlogManager";
import SettingsManager from "@/components/AdminComponents/Sections/SettingsManager";
import UserManager from "@/components/AdminComponents/Sections/UserManager";
import InquiriesManager from "@/components/AdminComponents/Sections/InquiriesManager";

const AdminDashboardPage = () => {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    const savedTab = localStorage.getItem("admin_active_tab");
    if (savedTab) setActiveTab(savedTab);
    setMounted(true);
  }, []);

  useEffect(() => {
    if (mounted) {
      localStorage.setItem("admin_active_tab", activeTab);
    }
  }, [activeTab, mounted]);

  const handleLogout = async () => {
    try {
      await fetch("/api/auth/logout", { method: "POST" });
      window.location.href = "/admin/login";
    } catch (e) {
      console.error("Logout failed", e);
      // fallback
      window.location.href = "/admin/login";
    }
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsSidebarOpen(false);
      }
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  if (!mounted) return null;

  const renderContent = () => {
    switch (activeTab) {
      case "dashboard":
        return <DashboardHome />;
      case "users":
        return <UserManager />;
      case "services":
        return <ServicesManager />;
      case "portfolio":
        return <PortfolioManager />;
      case "blog":
        return <BlogManager />;
      case "inquiries":
        return <InquiriesManager />;
      case "hero":
        return <HeroManager />;
      case "contact":
        return <ContactManager />;
      case "about":
        return <AboutManager />;
      case "settings":
        return <SettingsManager />;
      default:
        return <DashboardHome />;
    }
  };

  return (
    <div className="flex min-h-[calc(100vh-80px)] relative">
      <Sidebar
        activeTab={activeTab}
        setActiveTab={setActiveTab}
        isOpen={isSidebarOpen}
        setIsOpen={setIsSidebarOpen}
        onLogout={handleLogout}
      />

      <main
        className={`flex-1 transition-all duration-300  ml-20 ${
          isSidebarOpen ? "md:ml-64" : ""
        }`}
      >
        <div className="bg-white/95 dark:bg-gray-900/95 backdrop-blur-2xl min-h-[calc(100vh-3rem)] p-6 md:p-8 relative overflow-hidden">
          {/* Accent Line */}
          <div className="absolute top-0 left-0 w-full" />
          <div className="relative z-10">{renderContent()}</div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboardPage;

import { Metadata } from "next";
import { redirect } from "next/navigation";
import { cookies } from "next/headers";
import { jwtVerify } from "jose";

export const metadata: Metadata = {
  title: "Admin Dashboard | Growthik Media",
};

// Use the exact same secret strategy as API and Middleware
const JWT_SECRET_STR =
  process.env.NEXTAUTH_SECRET || "growthik_media_secret_secure_key_2024";
const JWT_SECRET = new TextEncoder().encode(JWT_SECRET_STR);

export default async function ProtectedAdminLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  const cookieStore = await cookies();
  const token = cookieStore.get("authToken")?.value;

  if (!token) {
    redirect("/admin/login");
  }

  try {
    const { payload } = await jwtVerify(token, JWT_SECRET);

    // Allow both roles
    if (payload.role !== "ADMIN" && payload.role !== "SUPER_ADMIN") {
      console.log("[Layout] Forbidden role:", payload.role);
      redirect("/admin/login");
    }

    // Success - render the dashboard children
    return (
      <div className="min-h-screen w-full bg-[#0f172a] flex">
        <main className="w-full min-h-screen flex-1">{children}</main>
      </div>
    );
  } catch (err: any) {
    console.error("[Layout] Auth failed:", err.message);
    redirect("/admin/login");
  }
}

"use client";
import React, { useState } from "react";
import { useRouter } from "next/navigation";
import { FiShield, FiLock, FiUser, FiArrowRight } from "react-icons/fi";
import Link from "next/link";

export default function AdminLoginPage() {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [loginSuccess, setLoginSuccess] = useState(false);

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();

      if (data.success) {
        setLoginSuccess(true);
        router.push("/admin/dashboard");
      } else {
        setError(
          data.details
            ? `${data.error}: ${data.details}`
            : data.error || "Login failed",
        );
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#0f172a] flex items-center justify-center p-4">
      <div className="bg-[#1e293b] border border-slate-700/50 rounded-2xl shadow-2xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-red-600/20 rounded-full flex items-center justify-center mx-auto mb-4 text-red-500">
            <FiShield className="w-8 h-8" />
          </div>
          <h1 className="text-2xl font-bold text-white uppercase tracking-wider">
            Admin Portal
          </h1>
          <p className="text-slate-400 text-sm mt-1">
            Restricted Access. Authorized Personnel Only.
          </p>
        </div>

        {error && (
          <div className="mb-4 p-3 bg-red-500/10 border border-red-500/50 text-red-500 text-sm rounded-lg text-center animate-shake">
            {error}
          </div>
        )}

        {loginSuccess && (
          <div className="mb-4 p-3 bg-emerald-500/10 border border-emerald-500/50 text-emerald-500 text-sm rounded-lg text-center flex items-center justify-center gap-2">
            <span className="w-2 h-2 bg-emerald-500 rounded-full animate-pulse" />
            Login Successful! Redirecting...
          </div>
        )}

        <form
          onSubmit={handleLogin}
          className={`space-y-6 ${loginSuccess ? "opacity-50 pointer-events-none" : ""}`}
        >
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Email Address
              </label>
              <div className="relative">
                <span className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500">
                  <FiUser />
                </span>
                <input
                  type="email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="w-full pl-10 pr-4 py-3 bg-[#0f172a] border border-slate-700 rounded-xl text-white focus:outline-none focus:border-red-500 transition-all shadow-inner"
                  placeholder="admin@growthikmedia.com"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Password
              </label>
              <div className="relative">
                <span className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-500">
                  <FiLock />
                </span>
                <input
                  type="password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full pl-10 pr-4 py-3 bg-[#0f172a] border border-slate-700 rounded-xl text-white focus:outline-none focus:border-red-500 transition-all shadow-inner"
                  placeholder="••••••••"
                />
              </div>
            </div>
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-red-600 hover:bg-red-700 text-white font-bold py-3.5 rounded-xl transition-all flex items-center justify-center gap-2 shadow-lg shadow-red-900/20 active:scale-95 disabled:opacity-50"
          >
            {loading ? (
              "Authenticating..."
            ) : (
              <>
                Login to Dashboard <FiArrowRight />
              </>
            )}
          </button>
        </form>

        <div className="mt-8 pt-6 border-t border-slate-700/50 text-center">
          <Link
            href="/"
            className="text-xs text-slate-500 hover:text-slate-300 transition-colors"
          >
            Return to Public Website
          </Link>
        </div>
      </div>
    </div>
  );
}

import { NextRequest, NextResponse } from "next/server";
export const dynamic = "force-dynamic";
import { db } from "@/lib/db";
import bcrypt from "bcryptjs";
import { SignJWT } from "jose";
import { cookies } from "next/headers";

// Important: Use a consistent secret strategy
const JWT_SECRET_STR =
  process.env.NEXTAUTH_SECRET || "growthik_media_secret_secure_key_2024";
const JWT_SECRET = new TextEncoder().encode(JWT_SECRET_STR);

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { email, password } = body;

    if (!email || !password) {
      return NextResponse.json(
        { success: false, error: "Email and password are required" },
        { status: 400 },
      );
    }

    console.log("[Login API] Attempt for:", email);

    // Check if Admin exists
    const admin = await db.admin.findUnique({
      where: { email },
    });

    if (!admin || !admin.password) {
      console.log("[Login API] User not found:", email);
      return NextResponse.json(
        { success: false, error: "Invalid credentials" },
        { status: 401 },
      );
    }

    // Verify password
    const passwordMatch = await bcrypt.compare(password, admin.password);
    if (!passwordMatch) {
      console.log("[Login API] Password mismatch for:", email);
      return NextResponse.json(
        { success: false, error: "Invalid credentials" },
        { status: 401 },
      );
    }

    if (!admin.isActive) {
      return NextResponse.json(
        { success: false, error: "Account is disabled" },
        { status: 403 },
      );
    }

    // Generate JWT
    const token = await new SignJWT({
      id: admin.id,
      email: admin.email,
      role: admin.role,
    })
      .setProtectedHeader({ alg: "HS256" })
      .setIssuedAt()
      .setExpirationTime("24h")
      .sign(JWT_SECRET);

    console.log("[Login API] Token generated, setting cookie...");

    // Set cookie
    const cookieStore = await cookies();
    cookieStore.set("authToken", token, {
      httpOnly: true,
      secure: process.env.NODE_ENV === "production",
      sameSite: "lax",
      maxAge: 60 * 60 * 24, // 24 hours
      path: "/",
    });

    return NextResponse.json(
      {
        success: true,
        message: "Login successful",
      },
      { status: 200 },
    );
  } catch (error: any) {
    console.error("[Login API] Fatal error:", error.message);
    return NextResponse.json(
      {
        success: false,
        error: "Internal server error",
        details: error.message,
      },
      { status: 500 },
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { db } from "@/lib/db";
import { jwtVerify } from "jose";

const JWT_SECRET = new TextEncoder().encode(
  process.env.JWT_SECRET || "your-secret-key-change-in-production"
);

export async function POST(request: NextRequest) {
  const response = NextResponse.json(
    { success: true, message: "Logged out successfully" },
    { status: 200 }
  );

  response.cookies.delete("authToken");
  return response;
}

export async function GET(request: NextRequest) {
  const response = NextResponse.json(
    { success: true, message: "Logged out successfully" },
    { status: 200 }
  );

  response.cookies.delete("authToken");
  return response;
}

import { NextRequest, NextResponse } from "next/server";
import { jwtVerify } from "jose";

const JWT_SECRET = new TextEncoder().encode(
  process.env.JWT_SECRET || "your-secret-key-change-in-production"
);

export async function GET(request: NextRequest) {
  try {
    const token = request.cookies.get("authToken")?.value;

    if (!token) {
      return NextResponse.json(
        { success: false, error: "Not authenticated" },
        { status: 401 }
      );
    }

    const verified = await jwtVerify(token, JWT_SECRET);
    const user = verified.payload as any;

    return NextResponse.json(
      {
        success: true,
        user: {
          id: user.id,
          email: user.email,
          role: user.role,
        },
      },
      { status: 200 }
    );
  } catch (error) {
    return NextResponse.json(
      { success: false, error: "Session invalid" },
      { status: 401 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { db as prisma } from "@/lib/db";

export const dynamic = "force-dynamic";

export async function POST(request: Request) {
  try {
    const { name, email, phone, service } = await request.json();

    const inquiry = await prisma.inquiry.create({
      data: {
        name,
        email,
        phone,
        message: `Service interest: ${service}`,
        subject: "Progressive Lead Capture",
        status: "NEW",
      },
    });

    return NextResponse.json({ success: true, inquiry });
  } catch (error) {
    console.error("Save lead error:", error);
    return NextResponse.json(
      { error: "Internal Server Error" },
      { status: 500 },
    );
  }
}

