"use client";
import React, { useState, useEffect } from "react";
import { FiSave, FiGrid, FiPlus, FiTrash2, FiEdit2 } from "react-icons/fi";

interface Service {
  id: string;
  name: string;
  description: string;
  icon: string;
}

export default function ServicesManager() {
  const [loading, setLoading] = useState(false);
  const [services, setServices] = useState<Service[]>([
    {
      id: "1",
      name: "SEO Optimization",
      description: "Boost your search rankings",
      icon: "FiTrendingUp",
    },
    {
      id: "2",
      name: "Social Media",
      description: "Engage with your audience",
      icon: "FiSmartphone",
    },
    {
      id: "3",
      name: "Content Marketing",
      description: "Create compelling content",
      icon: "FiFileText",
    },
  ]);

  useEffect(() => {
    const saved = localStorage.getItem("services_data");
    if (saved) {
      setServices(JSON.parse(saved));
    }
  }, []);

  const handleSave = () => {
    setLoading(true);
    setTimeout(() => {
      localStorage.setItem("services_data", JSON.stringify(services));
      setLoading(false);
      alert("Services updated successfully!");
    }, 800);
  };

  const handleAddService = () => {
    const newService: Service = {
      id: Date.now().toString(),
      name: "New Service",
      description: "Service description goes here",
      icon: "FiActivity",
    };
    setServices([...services, newService]);
  };

  const handleRemoveService = (id: string) => {
    if (confirm("Are you sure you want to remove this service?")) {
      setServices(services.filter((s) => s.id !== id));
    }
  };

  const updateService = (id: string, field: keyof Service, value: string) => {
    setServices(
      services.map((s) => (s.id === id ? { ...s, [field]: value } : s))
    );
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8 animate-fade-in">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white flex items-center gap-3">
            <FiGrid className="text-purple-500" />
            Services Management
          </h2>
          <p className="text-gray-500 dark:text-gray-400 mt-1">
            Manage your service offerings ({services.length} active).
          </p>
        </div>
        <div className="flex gap-3">
          <button
            onClick={handleAddService}
            className="flex items-center gap-2 px-4 py-2.5 bg-gray-100 dark:bg-gray-800 text-gray-900 dark:text-white rounded-xl hover:bg-gray-200 dark:hover:bg-gray-700 transition"
          >
            <FiPlus />
            Add Service
          </button>
          <button
            onClick={handleSave}
            disabled={loading}
            className="flex items-center gap-2 px-6 py-2.5 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-xl hover:opacity-90 transition-opacity disabled:opacity-50 font-medium"
          >
            <FiSave />
            {loading ? "Saving..." : "Save Changes"}
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <div
            key={service.id}
            className="bg-white dark:bg-gray-800 rounded-2xl p-6 shadow-sm border border-gray-100 dark:border-gray-700 group hover:border-blue-500 transition-colors relative"
          >
            <button
              onClick={() => handleRemoveService(service.id)}
              className="absolute top-4 right-4 text-red-400 hover:text-red-500 opacity-0 group-hover:opacity-100 transition-opacity p-2 bg-red-50 dark:bg-red-900/20 rounded-lg"
              title="Remove Service"
            >
              <FiTrash2 />
            </button>

            <div className="space-y-4">
              <div>
                <label className="text-xs font-semibold uppercase tracking-wider text-gray-400 mb-1 block">
                  Service Name
                </label>
                <input
                  type="text"
                  value={service.name}
                  onChange={(e) =>
                    updateService(service.id, "name", e.target.value)
                  }
                  className="w-full bg-transparent text-lg font-bold border-b border-transparent hover:border-gray-300 focus:border-blue-500 focus:outline-none transition-colors"
                />
              </div>

              <div>
                <label className="text-xs font-semibold uppercase tracking-wider text-gray-400 mb-1 block">
                  Description
                </label>
                <textarea
                  value={service.description}
                  onChange={(e) =>
                    updateService(service.id, "description", e.target.value)
                  }
                  rows={2}
                  className="w-full bg-transparent text-gray-600 dark:text-gray-300 text-sm border-b border-transparent hover:border-gray-300 focus:border-blue-500 focus:outline-none transition-colors resize-none"
                />
              </div>

              <div>
                <label className="text-xs font-semibold uppercase tracking-wider text-gray-400 mb-1 block">
                  Icon Name (React Icons)
                </label>
                <input
                  type="text"
                  value={service.icon}
                  onChange={(e) =>
                    updateService(service.id, "icon", e.target.value)
                  }
                  className="w-full bg-gray-50 dark:bg-gray-900 px-3 py-2 rounded-lg text-sm font-mono text-gray-500"
                />
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

import React, { useEffect, useRef, useState } from "react";
import Image from "next/image";
import {
  FiHome,
  FiLayout,
  FiGrid,
  FiPhone,
  FiInfo,
  FiBriefcase,
  FiEdit,
  FiSettings,
  FiLogOut,
  FiChevronLeft,
  FiChevronRight,
  FiChevronDown,
  FiUsers,
  FiMail,
  FiMenu,
  FiLayers,
} from "react-icons/fi";
import { ThemeToggleButton } from "@/components/comman/header/ThemeToggleButton";

type TabId =
  | "dashboard"
  | "users"
  | "services"
  | "portfolio"
  | "blog"
  | "inquiries"
  | "hero"
  | "contact"
  | "about"
  | "settings";

interface SidebarProps {
  activeTab: string;
  setActiveTab: (tab: string) => void;
  isOpen: boolean;
  setIsOpen: (isOpen: boolean) => void;
  onLogout: () => void;
}

interface MenuItem {
  id: string;
  label: string;
  icon: React.ReactNode;
  children?: { id: string; label: string }[];
}

export default function Sidebar({
  activeTab,
  setActiveTab,
  isOpen,
  setIsOpen,
  onLogout,
}: SidebarProps) {
  const sidebarRef = useRef<HTMLElement>(null);
  const toggleButtonRef = useRef<HTMLButtonElement>(null);

  // State for expanded dropdown menus
  const [expandedMenus, setExpandedMenus] = useState<string[]>(["content"]); // Default open content

  const toggleSubMenu = (menuId: string) => {
    setExpandedMenus((prev) =>
      prev.includes(menuId)
        ? prev.filter((id) => id !== menuId)
        : [...prev, menuId]
    );
  };

  const menuItems: MenuItem[] = [
    { id: "dashboard", label: "Dashboard", icon: <FiHome /> },
    {
      id: "content",
      label: "Content Manager",
      icon: <FiLayers />,
      children: [
        { id: "services", label: "Services" },
        { id: "portfolio", label: "Portfolio" },
        { id: "blog", label: "Blog Posts" },
        { id: "hero", label: "Hero Section" },
        { id: "contact", label: "Contact Info" },
        { id: "about", label: "About Us" },
      ],
    },
    { id: "inquiries", label: "Inquiries", icon: <FiMail /> },
    { id: "users", label: "User Management", icon: <FiUsers /> },
    { id: "settings", label: "Settings", icon: <FiSettings /> },
  ];

  // Handle escape key to close sidebar on mobile
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === "Escape" && isOpen && window.innerWidth < 768) {
        setIsOpen(false);
        toggleButtonRef.current?.focus();
      }
    };

    document.addEventListener("keydown", handleEscape);
    return () => document.removeEventListener("keydown", handleEscape);
  }, [isOpen, setIsOpen]);

  // Trap focus within sidebar when open on mobile
  useEffect(() => {
    if (!isOpen || window.innerWidth >= 768) return;

    const sidebar = sidebarRef.current;
    if (!sidebar) return;

    const focusableElements = sidebar.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );
    const firstElement = focusableElements[0] as HTMLElement;
    const lastElement = focusableElements[
      focusableElements.length - 1
    ] as HTMLElement;

    const handleTabKey = (e: KeyboardEvent) => {
      if (e.key !== "Tab") return;

      if (e.shiftKey) {
        if (document.activeElement === firstElement) {
          lastElement?.focus();
          e.preventDefault();
        }
      } else {
        if (document.activeElement === lastElement) {
          firstElement?.focus();
          e.preventDefault();
        }
      }
    };

    document.addEventListener("keydown", handleTabKey);
    return () => document.removeEventListener("keydown", handleTabKey);
  }, [isOpen]);

  // Prevent body scroll when mobile sidebar is open
  useEffect(() => {
    if (isOpen && window.innerWidth < 768) {
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }

    return () => {
      document.body.style.overflow = "";
    };
  }, [isOpen]);

  const handleBackdropClick = () => {
    setIsOpen(false);
    toggleButtonRef.current?.focus();
  };

  const handleTabChange = (tab: string) => {
    setActiveTab(tab);
    // Close sidebar on mobile after selecting a tab
    if (window.innerWidth < 768) {
      setIsOpen(false);
    }
  };

  return (
    <>
      {/* Mobile Backdrop */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/60 z-40 md:hidden transition-opacity backdrop-blur-sm"
          onClick={handleBackdropClick}
          aria-hidden="true"
        />
      )}

      <aside
        ref={sidebarRef}
        role="navigation"
        aria-label="Main navigation"
        aria-expanded={isOpen}
        className={`fixed left-0 top-0 h-full bg-white dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 transition-all duration-300 z-50 flex flex-col ${
          isOpen ? "w-64 shadow-2xl md:shadow-none" : "w-20"
        } translate-x-0`}
      >
        {/* Header */}
        <div
          className={`h-16 flex items-center ${
            isOpen ? "justify-between px-4" : "justify-center"
          } border-b border-gray-200 dark:border-gray-800`}
        >
          {isOpen ? (
            <>
              <div className="flex items-center gap-3">
                <div className="relative w-8 h-8 flex-shrink-0">
                  <Image
                    src="/favicon.ico"
                    alt="Growthik Media"
                    fill
                    className="object-contain"
                  />
                </div>
                <span
                  className="text-xs font-bold bg-clip-text text-transparent bg-gradient-to-r from-[var(--color-primary)] to-[var(--color-primary-light)]"
                  style={{ fontFamily: "Rostex, sans-serif" }}
                >
                  Growthik Media
                </span>
              </div>
              <button
                ref={toggleButtonRef}
                onClick={() => setIsOpen(false)}
                className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 text-gray-500 transition-colors focus:outline-none focus:ring-2 focus:ring-[var(--color-primary)]"
                aria-label="Collapse sidebar"
                title="Collapse Sidebar"
              >
                <FiChevronLeft />
              </button>
            </>
          ) : (
            <button
              ref={toggleButtonRef}
              onClick={() => setIsOpen(true)}
              className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 text-gray-500 transition-colors focus:outline-none "
              aria-label="Expand sidebar"
              title="Expand Sidebar"
            >
              <FiMenu />
            </button>
          )}
        </div>

        {/* Menu */}
        <nav className="flex-1 overflow-y-auto py-4 space-y-1 px-3">
          {menuItems.map((item) => (
            <div key={item.id} className="space-y-1">
              {/* Parent Item */}
              <button
                onClick={() =>
                  item.children
                    ? toggleSubMenu(item.id)
                    : handleTabChange(item.id)
                }
                aria-label={item.label}
                aria-expanded={
                  item.children ? expandedMenus.includes(item.id) : undefined
                }
                className={`w-full flex items-center gap-3 px-3 py-3 rounded-xl transition-all duration-200 group focus:outline-none   ${
                  activeTab === item.id && !item.children
                    ? "bg-gradient-to-r from-[var(--color-primary)] to-[var(--color-primary-light)] text-white shadow-lg shadow-[var(--color-primary)]/30"
                    : "text-gray-600 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-800"
                } ${!isOpen && "justify-center"}`}
              >
                <span
                  className={`text-xl flex-shrink-0 ${
                    activeTab === item.id && !item.children
                      ? "text-white"
                      : "text-gray-500 group-hover:text-[var(--color-primary)]"
                  }`}
                >
                  {item.icon}
                </span>

                {isOpen && (
                  <div className="flex-1 flex items-center justify-between text-left">
                    <span className="font-medium whitespace-nowrap text-sm">
                      {item.label}
                    </span>
                    {item.children && (
                      <span className="text-gray-400">
                        {expandedMenus.includes(item.id) ? (
                          <FiChevronDown />
                        ) : (
                          <FiChevronRight />
                        )}
                      </span>
                    )}
                  </div>
                )}
              </button>

              {/* Child Items (Dropdown) */}
              {item.children && isOpen && expandedMenus.includes(item.id) && (
                <div className="pl-10 space-y-1 animate-fadeIn">
                  {item.children.map((child) => (
                    <button
                      key={child.id}
                      onClick={() => handleTabChange(child.id)}
                      className={`w-full flex items-center px-4 py-2 text-sm rounded-lg transition-colors ${
                        activeTab === child.id
                          ? "text-[var(--color-primary)] font-medium bg-[var(--color-primary)]/10"
                          : "text-gray-500 hover:text-[var(--color-primary)] hover:bg-gray-100 dark:hover:bg-gray-800"
                      }`}
                    >
                      <span className="w-1.5 h-1.5 rounded-full bg-current mr-2 opacity-50"></span>
                      {child.label}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ))}
        </nav>

        {/* Footer */}
        <div className="p-4 border-t border-gray-200 dark:border-gray-800 space-y-4">
          {/* Theme Toggle */}
          <div
            className={`flex items-center ${
              !isOpen ? "justify-center" : "justify-between"
            }`}
          >
            {isOpen && (
              <span className="text-sm font-medium text-gray-500 dark:text-gray-400">
                Theme
              </span>
            )}
            <div className={`${!isOpen ? "" : ""}`}>
              <ThemeToggleButton />
            </div>
          </div>

          {/* Profile Section */}
          <div
            className={`flex items-center gap-3 p-2 rounded-xl bg-gray-50 dark:bg-gray-800/50 border border-gray-100 dark:border-gray-700 transition-all ${
              !isOpen ? "justify-center" : ""
            }`}
          >
            <div className="relative w-8 h-8 rounded-full bg-white dark:bg-gray-800 flex items-center justify-center shrink-0 shadow-sm border border-gray-200 dark:border-gray-700 overflow-hidden">
              <Image
                src="/favicon.ico"
                alt="Admin"
                fill
                className="object-contain p-1"
              />
            </div>

            {isOpen && (
              <div className="flex-1 min-w-0">
                <p className="text-sm font-bold text-gray-900 dark:text-white truncate">
                  Administrator
                </p>
                <p className="text-xs text-gray-500 truncate">
                  admin@growthik.com
                </p>
              </div>
            )}

            {isOpen && (
              <button
                onClick={onLogout}
                className="p-1.5 rounded-lg text-gray-400 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-900/10 transition-colors"
                title="Logout"
              >
                <FiLogOut size={18} />
              </button>
            )}
          </div>

          {/* Mobile/Collapsed Logout Fallback */}
          {!isOpen && (
            <button
              onClick={onLogout}
              className="w-full flex justify-center p-2 rounded-xl text-gray-400 hover:text-red-500 hover:bg-red-50 dark:hover:bg-red-900/10 transition-colors"
              title="Logout"
            >
              <FiLogOut size={20} />
            </button>
          )}
        </div>
      </aside>
    </>
  );
}

"use client";

import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ChevronLeft, ChevronRight } from "lucide-react";

interface Slide {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  image: string;
}

const slides: Slide[] = [
  {
    id: 1,
    title: "Seamless Execution",
    subtitle: "Transforming ideas into impact with",
    description: "Let's flip your brand's challenges into success",
    image:
      "https://images.unsplash.com/photo-1497366216548-37526070297c?q=80&w=2670&auto=format&fit=crop",
  },
  {
    id: 2,
    title: "Synergistic Consulting",
    subtitle: "Transforming ideas into impact with",
    description: "Let's flip your brand's challenges into success",
    image:
      "https://images.unsplash.com/photo-1522071820081-009f0129c71c?q=80&w=2670&auto=format&fit=crop",
  },
  {
    id: 3,
    title: "Integrated Digital",
    subtitle: "Transforming ideas into impact with",
    description: "Let's flip your brand's challenges into success",
    image:
      "https://images.unsplash.com/photo-1460925895917-afdab827c52f?q=80&w=2426&auto=format&fit=crop",
  },
];

const CircularRevealSection = React.memo(() => {
  const [index, setIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [revealOrigin, setRevealOrigin] = useState("50% 50%");

  const navigate = (dir: "next" | "prev", origin: string) => {
    if (isAnimating) return;
    setRevealOrigin(origin);
    if (dir === "next") {
      setIndex((prev) => (prev + 1) % slides.length);
    } else {
      setIndex((prev) => (prev - 1 + slides.length) % slides.length);
    }
  };

  // Auto-play
  useEffect(() => {
    const timer = setInterval(() => navigate("next", "50% 50%"), 7000);
    return () => clearInterval(timer);
  }, [index, isAnimating]);

  return (
    <section
      className="relative w-full h-[50vh] md:h-[70vh] overflow-hidden bg-black select-none border-y border-white/10 my-1"
      aria-label="Services Banner"
    >
      {/* Static Background (Previous Slide) */}
      <div
        className="absolute inset-0 w-full h-full bg-cover bg-center scale-105"
        style={{
          backgroundImage: `url(${slides[(index - 1 + slides.length) % slides.length].image})`,
        }}
      >
        <div className="absolute inset-0 bg-black/70 backdrop-blur-xs" />
      </div>

      <AnimatePresence initial={false} mode="wait">
        <motion.div
          key={index}
          initial={{ clipPath: `circle(0% at ${revealOrigin})` }}
          animate={{ clipPath: `circle(150% at ${revealOrigin})` }}
          exit={{ clipPath: `circle(0% at ${revealOrigin})` }}
          transition={{
            duration: 1.2,
            ease: [0.77, 0, 0.175, 1],
          }}
          onAnimationStart={() => setIsAnimating(true)}
          onAnimationComplete={() => setIsAnimating(false)}
          className="absolute inset-0 w-full h-full z-10 overflow-hidden"
        >
          {/* Main Slide Content */}
          <article
            className="absolute inset-0 w-full h-full bg-cover bg-center flex flex-col items-center justify-center text-center px-6"
            style={{ backgroundImage: `url(${slides[index].image})` }}
          >
            {/* Overlay */}
            <div className="absolute inset-0 bg-linear-to-b from-black/80 via-black/40 to-black/90" />

            {/* Content Container */}
            <div className="relative z-20 max-w-6xl mx-auto flex flex-col items-center">
              <motion.p
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.5, duration: 0.6 }}
                className="text-(--color-primary) text-xs md:text-lg font-bold tracking-[0.4em] uppercase mb-4"
              >
                {slides[index].subtitle}
              </motion.p>

              <motion.h2
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.7, duration: 0.8 }}
                className="text-white text-3xl sm:text-6xl md:text-8xl font-black mb-8 tracking-tighter leading-tight uppercase"
              >
                {slides[index].title}
              </motion.h2>

              <motion.div
                initial={{ opacity: 0, width: 0 }}
                animate={{ opacity: 1, width: "80px" }}
                transition={{ delay: 1, duration: 0.6 }}
                className="h-1 bg-(--color-primary) mb-8"
              />

              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 1.2, duration: 0.8 }}
                className="text-white/70 text-base md:text-xl font-medium max-w-xl mx-auto leading-relaxed"
              >
                {slides[index].description}
              </motion.p>
            </div>
          </article>
        </motion.div>
      </AnimatePresence>

      {/* Navigation Indicators */}
      <div className="absolute bottom-10 left-1/2 -translate-x-1/2 z-40 flex items-center gap-3">
        {slides.map((_, i) => (
          <button
            key={i}
            onClick={() => !isAnimating && setIndex(i)}
            className={`h-1.5 transition-all duration-500 rounded-full cursor-pointer ${index === i ? "w-10 bg-(--color-primary)" : "w-3 bg-white/20 hover:bg-white/40"}`}
            aria-label={`Go to slide ${i + 1}`}
          />
        ))}
      </div>

      {/* Decorative Branding */}
      <div className="absolute top-8 right-8 z-40">
        <span className="text-white/20 text-[10px] font-bold tracking-[0.5em] uppercase">
          Growthik
        </span>
      </div>

      {/* Interactive Navigation Zones (Arrows) */}
      <div className="absolute inset-0 z-30 flex pointer-events-none">
        <div
          className="w-1/4 h-full cursor-pointer pointer-events-auto group relative flex items-center justify-start pl-6 md:pl-10"
          onClick={(e) => navigate("prev", `${e.clientX}px ${e.clientY}px`)}
        >
          <div className="p-3 rounded-full border border-white/10 hover:border-(--color-primary) hover:bg-(--color-primary) transition-all duration-300 opacity-0 group-hover:opacity-100">
            <ChevronLeft className="text-white w-5 h-5" />
          </div>
        </div>
        <div className="flex-1" />
        <div
          className="w-1/4 h-full cursor-pointer pointer-events-auto group relative flex items-center justify-end pr-6 md:pr-10"
          onClick={(e) => navigate("next", `${e.clientX}px ${e.clientY}px`)}
        >
          <div className="p-3 rounded-full border border-white/10 hover:border-(--color-primary) hover:bg-(--color-primary) transition-all duration-300 opacity-0 group-hover:opacity-100">
            <ChevronRight className="text-white w-5 h-5" />
          </div>
        </div>
      </div>

      <style jsx>{`
        .font-handwriting {
          font-family: var(--font-caveat), cursive;
        }
      `}</style>
    </section>
  );
});

