"use client";
import React, { useState, useEffect } from "react";
import Sidebar from "@/components/AdminComponents/Sidebar";
import DashboardHome from "@/components/AdminComponents/DashboardHome";
import HeroManager from "@/components/AdminComponents/Sections/HeroManager";
import ServicesManager from "@/components/AdminComponents/Sections/ServicesManager";
import ContactManager from "@/components/AdminComponents/Sections/ContactManager";
import AboutManager from "@/components/AdminComponents/Sections/AboutManager";
import PortfolioManager from "@/components/AdminComponents/Sections/PortfolioManager";
import BlogManager from "@/components/AdminComponents/Sections/BlogManager";
import SettingsManager from "@/components/AdminComponents/Sections/SettingsManager";
import UserManager from "@/components/AdminComponents/Sections/UserManager";
import InquiriesManager from "@/components/AdminComponents/Sections/InquiriesManager";

const AdminDashboardPage = () => {
  const [activeTab, setActiveTab] = useState("dashboard");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    const savedTab = localStorage.getItem("admin_active_tab");
    if (savedTab) setActiveTab(savedTab);
    setMounted(true);
  }, []);

  useEffect(() => {
    if (mounted) {
      localStorage.setItem("admin_active_tab", activeTab);
    }
  }, [activeTab, mounted]);

  const handleLogout = async () => {
    try {
      await fetch("/api/auth/logout", { method: "POST" });
      window.location.href = "/admin/login";
    } catch (e) {
      console.error("Logout failed", e);
      // fallback
      window.location.href = "/admin/login";
    }
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsSidebarOpen(false);
      }
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  if (!mounted) return null;

  const renderContent = () => {
    switch (activeTab) {
      case "dashboard":
        return <DashboardHome />;
      case "users":
        return <UserManager />;
      case "services":
        return <ServicesManager />;
      case "portfolio":
        return <PortfolioManager />;
      case "blog":
        return <BlogManager />;
      case "inquiries":
        return <InquiriesManager />;
      case "hero":
        return <HeroManager />;
      case "contact":
        return <ContactManager />;
      case "about":
        return <AboutManager />;
      case "settings":
        return <SettingsManager />;
      default:
        return <DashboardHome />;
    }
  };

  return (
    <div className="flex min-h-[calc(100vh-80px)] relative">
      <Sidebar
        activeTab={activeTab}
        setActiveTab={setActiveTab}
        isOpen={isSidebarOpen}
        setIsOpen={setIsSidebarOpen}
        onLogout={handleLogout}
      />

      <main
        className={`flex-1 transition-all duration-300  ml-20 ${
          isSidebarOpen ? "md:ml-64" : ""
        }`}
      >
        <div className="bg-white/95 dark:bg-gray-900/95 backdrop-blur-2xl min-h-[calc(100vh-3rem)] p-6 md:p-8 relative overflow-hidden">
          {/* Accent Line */}
          <div className="absolute top-0 left-0 w-full" />
          <div className="relative z-10">{renderContent()}</div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboardPage;

import { Metadata } from "next";
import { headers } from "next/headers";
import { jwtVerify } from "jose";
import { redirect } from "next/navigation";
import { ThemeToggleButton } from "@/components/comman/header/ThemeToggleButton";

export const metadata: Metadata = {
  title: "Admin Dashboard - Growthik Media",
};

const JWT_SECRET = new TextEncoder().encode(
  process.env.JWT_SECRET || "your-secret-key-change-in-production"
);

export default async function ProtectedAdminLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  // Get token from cookies via headers
  const headersList = await headers();
  const cookieHeader = headersList.get("cookie") || "";

  let token = "";
  const cookies = cookieHeader.split(";");
  for (const cookie of cookies) {
    const [name, value] = cookie.trim().split("=");
    if (name === "authToken") {
      token = value;
      break;
    }
  }

  // Verify token and check admin role
  let user: any = null;
  if (token) {
    try {
      const verified = await jwtVerify(token, JWT_SECRET);
      user = verified.payload;

      // Check if user is admin
      if (user.role !== "ADMIN") {
        redirect("/");
      }
    } catch (error) {
      // Token invalid
      redirect("/admin/login");
    }
  } else {
    redirect("/admin/login");
  }

  return (
    <div className="min-h-screen w-full bg-gradient-to-br from-gray-50 to-gray-100 dark:from-[#0A0A0A] dark:to-[#1a1a1a] flex">
      <main className="w-full min-h-screen flex-1">{children}</main>
    </div>
  );
}

"use client";
import React, { useState } from "react";
import { useRouter } from "next/navigation";
import { FiShield, FiLock, FiArrowRight } from "react-icons/fi";
import Link from "next/link";

export default function AdminLoginPage() {
  const router = useRouter();
  const [identifier, setIdentifier] = useState(""); // Email
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [step, setStep] = useState<"identifier" | "otp">("identifier");
  const [otp, setOtp] = useState(["", "", "", "", "", ""]);

  // Handle identifier submission (Request OTP)
  const handleSendOtp = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: identifier }),
      });
      const data = await res.json();

      if (data.success) {
        setStep("otp");
      } else {
        setError(data.error || "Failed to send OTP");
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // Handle OTP submission (Verify OTP)
  const handleVerify = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    const code = otp.join("");
    if (code.length !== 6) {
      setError("Please enter complete OTP");
      setLoading(false);
      return;
    }

    try {
      const res = await fetch("/api/auth/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: identifier, code }),
      });
      const data = await res.json();

      if (data.success) {
        // Redirect to dashboard
        router.push("/admin/dashboard");
        router.refresh();
      } else {
        setError(data.error || "Invalid OTP");
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleOtpChange = (element: HTMLInputElement, index: number) => {
    if (isNaN(Number(element.value))) return false;
    setOtp([...otp.map((d, idx) => (idx === index ? element.value : d))]);
    if (element.nextSibling && element.value !== "") {
      (element.nextSibling as HTMLInputElement).focus();
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
      <div className="bg-gray-800 border border-gray-700 rounded-2xl shadow-2xl p-8 w-full max-w-md">
        <div className="text-center mb-8">
          <div className="w-16 h-16 bg-red-600/20 rounded-full flex items-center justify-center mx-auto mb-4 text-red-500">
            <FiShield className="w-8 h-8" />
          </div>
          <h1 className="text-2xl font-bold text-white uppercase tracking-wider">
            Admin Portal
          </h1>
          <p className="text-gray-400 text-sm mt-1">
            Restricted Access. Authorized Personnel Only.
          </p>
        </div>

        {error && (
          <div className="mb-4 p-3 bg-red-500/10 border border-red-500/50 text-red-500 text-sm rounded-lg text-center">
            {error}
          </div>
        )}

        {step === "identifier" ? (
          <form onSubmit={handleSendOtp} className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Admin Email
              </label>
              <input
                type="email"
                required
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                className="w-full px-4 py-3 bg-gray-900 border border-gray-700 rounded-lg text-white focus:outline-none focus:border-red-500 focus:ring-1 focus:ring-red-500 transition-colors"
                placeholder="admin@growthikmedia.com"
              />
            </div>
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-red-600 hover:bg-red-700 text-white font-semibold py-3 rounded-lg transition-all flex items-center justify-center gap-2"
            >
              {loading ? (
                "Sending OTP..."
              ) : (
                <>
                  Send OTP <FiArrowRight />
                </>
              )}
            </button>
          </form>
        ) : (
          <form onSubmit={handleVerify} className="space-y-6">
            <div>
              <label className="block text-center text-sm font-medium text-gray-300 mb-4">
                Enter OTP sent to {identifier}
              </label>
              <div className="flex justify-center gap-2">
                {otp.map((data, index) => (
                  <input
                    key={index}
                    type="text"
                    maxLength={1}
                    value={data}
                    onChange={(e) => handleOtpChange(e.target, index)}
                    onFocus={(e) => e.target.select()}
                    className="w-10 h-12 border border-gray-600 rounded bg-gray-900 text-white text-center text-lg font-bold focus:border-red-500 focus:outline-none"
                  />
                ))}
              </div>
            </div>
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-red-600 hover:bg-red-700 text-white font-semibold py-3 rounded-lg transition-all flex items-center justify-center gap-2"
            >
              {loading ? (
                "Verifying..."
              ) : (
                <>
                  Verify & Login <FiLock />
                </>
              )}
            </button>
            <div className="text-center">
              <button
                type="button"
                onClick={() => setStep("identifier")}
                className="text-xs text-gray-500 hover:text-gray-300"
              >
                Back to Email
              </button>
            </div>
          </form>
        )}

        <div className="mt-8 pt-6 border-t border-gray-700 text-center">
          <Link href="/" className="text-xs text-gray-500 hover:text-gray-300">
            &larr; Exit to Admin Login
          </Link>
        </div>
      </div>
    </div>
  );
}

