          <Link
            href="/admin/login"
            className="block w-full text-center bg-[var(--color-primary)] text-white py-2.5 rounded-full font-medium transition hover:opacity-90"
          >
            Admin Login
          </Link>

          <Link
            href="/admin/dashboard"
            className="block w-full text-center border border-red-500 text-red-600 py-2.5 rounded-full font-medium transition hover:bg-red-50 dark:hover:bg-white/10"
          >
            Go to Dashboard
          </Link>
        </div>
      </section>
    </main>
  );
}

import { NextRequest, NextResponse } from "next/server";
import { db } from "@/lib/db";
import {
  generateOTP,
  getOTPExpiration,
  sendOTPEmail,
  validateEmail,
} from "@/lib/auth";

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { email } = body;

    if (!email) {
      return NextResponse.json(
        { success: false, error: "Email is required" },
        { status: 400 }
      );
    }

    if (!validateEmail(email)) {
      return NextResponse.json(
        { success: false, error: "Invalid email format" },
        { status: 400 }
      );
    }

    // Check if Admin exists
    const admin = await db.admin.findUnique({
      where: { email },
    });

    if (!admin) {
      // Security: Don't reveal if admin exists, but here we can return 401 for clarity in this specific admin-portal context
      return NextResponse.json(
        { success: false, error: "Access denied. Not an admin." },
        { status: 403 }
      );
    }

    // Generate OTP (Dummy for now)
    const otp = "123456";
    const expiresAt = getOTPExpiration();

    // Invalidate old OTPs for this email
    await db.otp.deleteMany({
      where: { email, isUsed: false },
    });

    // Create new OTP
    await db.otp.create({
      data: {
        email,
        code: otp,
        expires: expiresAt,
      },
    });

    // Try to send email but don't fail if it fails (since we are using dummy OTP)
    try {
      await sendOTPEmail(email, otp);
    } catch (e) {
      console.warn("Email sending failed but continuing with dummy OTP flow");
    }

    return NextResponse.json(
      {
        success: true,
        message: "OTP sent (Dummy: 123456)",
        type: "email",
      },
      { status: 200 }
    );
  } catch (error) {
    console.error("Login OTP Request error:", error);
    return NextResponse.json(
      { success: false, error: "Internal server error" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { db } from "@/lib/db";
import { jwtVerify } from "jose";

const JWT_SECRET = new TextEncoder().encode(
  process.env.JWT_SECRET || "your-secret-key-change-in-production"
);

export async function POST(request: NextRequest) {
  const response = NextResponse.json(
    { success: true, message: "Logged out successfully" },
    { status: 200 }
  );

  response.cookies.delete("authToken");
  return response;
}

export async function GET(request: NextRequest) {
  const response = NextResponse.json(
    { success: true, message: "Logged out successfully" },
    { status: 200 }
  );

  response.cookies.delete("authToken");
  return response;
}

import { NextRequest, NextResponse } from "next/server";
import { jwtVerify } from "jose";

const JWT_SECRET = new TextEncoder().encode(
  process.env.JWT_SECRET || "your-secret-key-change-in-production"
);

export async function GET(request: NextRequest) {
  try {
    const token = request.cookies.get("authToken")?.value;

    if (!token) {
      return NextResponse.json(
        { success: false, error: "Not authenticated" },
        { status: 401 }
      );
    }

    const verified = await jwtVerify(token, JWT_SECRET);
    const user = verified.payload as any;

    return NextResponse.json(
      {
        success: true,
        user: {
          id: user.id,
          email: user.email,
          role: user.role,
        },
      },
      { status: 200 }
    );
  } catch (error) {
    return NextResponse.json(
      { success: false, error: "Session invalid" },
      { status: 401 }
    );
  }
}

