  const toggleFaq = (id: number) => {
    setOpenId(openId === id ? null : id);
  };

  return (
    <section
      id="faq"
      className="relative py-24 px-4 overflow-hidden"
      style={{
        backgroundColor: "var(--background)",
      }}
    >
