"use client";

import { useTheme } from "next-themes";
import { useEffect, useState } from "react";
import {
  Mail,
  Phone,
  ArrowUp,
  Linkedin,
  Instagram,
  Facebook,
  Twitter,
  Send,
  MapPin,
  ExternalLink,
} from "lucide-react";
import Image from "next/image";
import {
  FiGlobe,
  FiCode,
  FiPenTool,
  FiLayers,
  FiHome,
  FiInfo,
  FiBookOpen,
  FiMail,
  FiShoppingCart,
} from "react-icons/fi";
import { images } from "@/app/assets/images/images";
import { CONTACT_INFO } from "@/constants/contact";

export default function Footer() {
  const { theme } = useTheme();
  const [mounted, setMounted] = useState(false);
  const [email, setEmail] = useState("");
  const [showScrollTop, setShowScrollTop] = useState(false);

  useEffect(() => {
    setMounted(true);

    const handleScroll = () => {
      setShowScrollTop(window.scrollY > 400);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleSubscribe = () => {
    if (email && email.includes("@")) {
      console.log("Subscribing:", email);
      alert(`ðŸŽ‰ Thank you for subscribing to ${CONTACT_INFO.companyName}!`);
      setEmail("");
    } else {
      alert("âš ï¸ Please enter a valid email address");
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      handleSubscribe();
    }
  };

  if (!mounted) return null;

  const isDark = theme === "dark";

  const quickLinks = [
    { href: "/", label: "Home", icon: FiHome },
    { href: "/about", label: "About", icon: FiInfo },
    { href: "/portfolio", label: "Portfolio", icon: FiLayers },
    { href: "/blog", label: "Blog", icon: FiBookOpen },
    { href: "/contact", label: "Contact", icon: FiMail },
  ];

  const topServices = [
    {
      href: "/services/digital-marketing",
      label: "Digital Marketing",
      icon: FiGlobe,
    },
    {
      href: "/services/website-development",
      label: "Website Development",
      icon: FiCode,
    },
    { href: "/services/seo", label: "SEO Services", icon: FiGlobe },
    {
      href: "/services/branding-consulting",
      label: "Branding & Design",
      icon: FiPenTool,
    },
    {
      href: "/services/ecommerce-development",
      label: "E-Commerce Solutions",
      icon: FiShoppingCart,
    },
    {
      href: "/services/social-media-marketing",
      label: "Social Media Marketing",
      icon: FiGlobe,
    },
  ];

  return (
    <>
      <footer
        className={`transition-all duration-500 relative ${
          isDark
            ? "bg-[#0A0A0A] text-white"
            : "bg-gradient-to-br from-gray-50 via-white to-gray-100 text-gray-900"
        }`}
      >
        {/* Decorative Background Elements */}
        <div className="absolute inset-0 opacity-5 pointer-events-none">
          <div className="absolute top-0 right-0 w-96 h-96 bg-[#D90B1C] rounded-full blur-3xl"></div>
          <div className="absolute bottom-0 left-0 w-96 h-96 bg-[#F22E52] rounded-full blur-3xl"></div>
        </div>

        {/* Newsletter Section with Enhanced Design */}
        <div
          className={`relative ${
            isDark
              ? "bg-gradient-to-r from-[#D90B1C] via-[#F22E52] to-[#D90B1C]"
              : "bg-gradient-to-r from-[#D90B1C] via-[#F22E52] to-[#D90B1C]"
          } transition-all duration-500`}
        >
          {/* Animated Background Pattern */}
          <div className="absolute inset-0 opacity-10">
            <div
              className="absolute inset-0"
              style={{
                backgroundImage: `radial-gradient(circle at 2px 2px, white 1px, transparent 0)`,
                backgroundSize: "40px 40px",
              }}
            ></div>
          </div>

          <div className="max-w-7xl mx-auto  h-1 bg-[var(--bg-color)] red-line-left"></div>
        </div>

        {/* Main Footer Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 md:py-15 relative z-10">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-12 gap-12 lg:gap-8">
            {/* Brand Column - Spans 4 columns on large screens */}
            <div className="lg:col-span-4">
              <div className="mb-6">
                {/* Logo */}
                <div className="flex items-center gap-3 mb-4">
                  <div className="relative w-12 h-12  rounded-xl flex items-center justify-center ">
                    <span className="text-white font-bold text-2xl">
                      <img src={images.icon} alt="" />
                    </span>
                  </div>
                  <div>
                    <h3 className="text-2xl md:text-3xl font-bold tracking-tight bg-gradient-to-r from-[#D90B1C] to-[#F22E52] bg-clip-text text-transparent">
                      {CONTACT_INFO.companyName}
                    </h3>
                    <p
                      className={`text-sm font-medium ${
                        isDark ? "text-gray-400" : "text-gray-600"
                      }`}
                    >
                      Digital Growth Partners
                    </p>
                  </div>
                </div>

                <p
                  className={`text-base leading-relaxed mb-6 ${
                    isDark ? "text-gray-400" : "text-gray-600"
                  }`}
                >
                  Empowering businesses to thrive in the digital landscape
                  through innovative marketing strategies, cutting-edge web
                  development, and creative branding solutions.
                </p>

                {/* Contact Info */}
                <div className="space-y-3">
                  <a
                    href={`tel:${CONTACT_INFO.phone.primary}`}
                    className={`flex items-center gap-3 group ${
                      isDark ? "hover:text-[#D90B1C]" : "hover:text-[#D90B1C]"
                    } transition-colors`}
                  >
                    <div
                      className={`p-2 rounded-lg ${
                        isDark ? "bg-gray-800" : "bg-gray-100"
                      } group-hover:bg-[#D90B1C] transition-colors`}
                    >
                      <Phone className="w-4 h-4 group-hover:text-white" />
                    </div>
                    <span className="text-sm font-medium">
                      {CONTACT_INFO.phone.primary}
                    </span>
                  </a>
                  <a
                    href={`mailto:${CONTACT_INFO.email.info}`}
                    className={`flex items-center gap-3 group ${
                      isDark ? "hover:text-[#D90B1C]" : "hover:text-[#D90B1C]"
