    setTheme(theme === "light" ? "dark" : "light");
  };

  // Mobile regular link component
  const MobileRegularLink = ({
    href,
    label,
    icon: Icon,
  }: {
    href: string;
    label: string;
    icon?: any;
  }) => (
    <li>
      <Link
