                <circle cx="10" cy="16" r="1.5" />
              </svg>
            </button>
          </div>
        </div>

        {/* Post Content */}
        <div className="p-4">
          <p className="text-gray-800 dark:text-[var(--text-primary)] text-sm sm:text-base mb-3">
